package org.shipstone.demo.cache.commons;

public class Constants {

  /**
   * Pattern de formatage de la date (JSON)
   */
  public static final String JSON_DATE_PATTERN = "yyyyMMdd";

  /**
   * Pattern de formatage de l'heure (JSON)
   */
  public static final String JSON_TIME_PATTERN = "HHmmss";

  /**
   * Pattern de formatage d'un timestamp
   */
  public static final String JSON_TIMESTAMP_PATTERN = JSON_DATE_PATTERN + JSON_TIME_PATTERN;

}
